import { InputBase, Paper, Button, IconButton } from '@material-ui/core';
import React from 'react';
import ClearIcone from '@material-ui/icons/Clear';
import { fade, makeStyles } from '@material-ui/core/styles';




const useStyle = makeStyles((theme)=>({

    card: {
       
        margin: theme.spacing(0,1,1,1),
        paddingBottom: theme.spacing(4),
    },
    input: {
        margin:theme.spacing(1),
    },
    btnConfirm: {
        background:'#5AAC44',
        color:'#fff',
        '&:hover':{
            background:fade('#5AAC44',0.75),
        },
    },
    confirm: {
        margin: theme.spacing(0,1,1,1),

    },
}))

export default function InputCard(){
    const classes = useStyle();
    return(
        <div>
            <div>
            <Paper className={classes.card}>
                <InputBase multiline fullWidth inputProps={{className: classes.input,}}
                placeholder="Enter a title of this card.."
                />
            </Paper>
            </div>
            <div className={classes.confirm}>
                <button className={classes.btnConfirm}>Add card</button>
                <IconButton>
                    <ClearIcone />
                </IconButton>
            </div>
            
        </div>
    );
}
