import { InputBase, Paper, Button, IconButton } from '@material-ui/core';
import React, { useContext, useState } from 'react';
import ClearIcone from '@material-ui/icons/Clear';
import { fade, makeStyles } from '@material-ui/core/styles';
import storeApi from '../../utils/storeApi';




const useStyle = makeStyles((theme)=>({

    card: {
       
        margin: theme.spacing(0,1,1,1),
        paddingBottom: theme.spacing(4),
    },
    input: {
        margin:theme.spacing(1),
    },
    btnConfirm: {
        background:'#5AAC44',
        color:'#fff',
        '&:hover':{
            background:fade('#5AAC44',0.75),
        },
    },
    confirm: {
        margin: theme.spacing(0,1,1,1),

    },
}))

export default function InputCard( {setOpen, listId, type} ){
    const classes = useStyle();
    const {addMoreCard} = useContext(storeApi);
    const [title,setTitle ] = useState('');
    const handleOnChange = (e) => {
        setTitle(e.target.value);
    };
    const handleBtnconfirm = () =>{
        addMoreCard(title, listId);
        setTitle('');
        setOpen(false);
    };

    const handleBlur = () => {
        setOpen(false);
        setTitle('');

    };
    return(
        <div>
            <div>
            <Paper className={classes.card}>
                <InputBase 
                onChange={handleOnChange}
                multiline
                onBlur={handleBlur} 
                fullWidth inputProps={{className: classes.input,}}
                value={title}
                placeholder={type ==='card'?"Enter a title of this card..":"Enter list title.."}
                />
            </Paper>
            </div>
            <div className={classes.confirm}>
                <button className={classes.btnConfirm} onClick={handleBtnconfirm}>
                    {type === 'card'?"Add card" : "Add list"}</button>
                <IconButton onClick={() => setOpen(false)}>
                    <ClearIcone />
                </IconButton>
            </div>
            
        </div>
    );
}
